      function copyText(textToCopy, type) {
    // Tạo textarea để sao chép văn bản
    var copyTextarea = document.createElement("textarea");
    copyTextarea.textContent = textToCopy;
    document.body.appendChild(copyTextarea);
    copyTextarea.select();
    document.execCommand('copy');
    document.body.removeChild(copyTextarea);
    
    // Hiển thị thông báo sau khi sao chép
    let message = '';
    if (type === 'stk') {
        message = 'Sao chép rồi nhớ bank đó nhaaa><';
    } else if (type === 'chutaikhoan') {
        message = 'Đã sao chép chủ tài khoản vào bộ nhớ tạm';
    }
    
    Toastify({
        text: message,
        duration: 3000,
        backgroundColor: "green",
        close: true
    }).showToast();
}

    // Gán sự kiện cho các phần tử có thuộc tính onclick chứa 'copyText'
    document.addEventListener('DOMContentLoaded', function() {
        document.querySelectorAll('span[onclick*="copyText"]').forEach(function(span) {
            span.addEventListener('click', function() {
                // Tìm văn bản cần sao chép từ thuộc tính onclick
                const textToCopy = this.textContent.match(/Số Tài Khoản: (\d+)/) 
                    ? this.textContent.match(/Số Tài Khoản: (\d+)/)[1]
                    : this.textContent.match(/Chủ Tài Khoản: (.+)/)[1];
                const type = this.textContent.includes('Số Tài Khoản') ? 'stk' : 'chutaikhoan';
                copyText(textToCopy, type);
            });
        });
    });

    // Hàm thông báo chung sử dụng SweetAlert
    function __ThanhDieuDev() {
        thanhDieuNoto();
    }

    function thanhDieuNoto() {
        swal({
            content: {
                element: "div",
                attributes: {
                    innerHTML: `

Thông Tin Thanh Toán !

Copy số tài khoản 1 cách dễ dàng và nhanh chóng.
                    `,
                },
            },
            buttons: {
                confirm: {
                    text: "OK",
                    className: "swal-button swal-button--confirm",
                },
            },
        });
    }

