let rand = Math.floor(Math.random() * Wussun_Music.length);
const audio = new Audio(Wussun_Music[rand]);
let popupDisplayed = false; // Biến trạng thái để theo dõi xem popup đã được hiển thị chưa

function createPopup(message) {
    const popup = document.createElement('div');
    popup.style.position = 'fixed';
    popup.style.top = '10px';
    popup.style.left = '50%';
    popup.style.transform = 'translateX(-50%)';
    popup.style.background = 'green';
    popup.style.color = '#fff';
    popup.style.padding = '10px';
    popup.style.borderRadius = '5px';
    popup.style.zIndex = '1000';
    popup.style.opacity = '0';
    popup.style.transition = 'opacity 0.5s ease-in-out';
    popup.textContent = message;
    document.body.appendChild(popup);

    // Hiển thị và ẩn popup sau 2 giây
    setTimeout(() => {
        popup.style.opacity = '1';
        setTimeout(() => {
            popup.style.opacity = '0';
            setTimeout(() => popup.remove(), 500); // Xóa popup sau khi ẩn
        }, 2000);
    }, 0);
}

function wussunplay() {
    if (!popupDisplayed) { // Kiểm tra nếu popup chưa được hiển thị
        audio.play();
        createPopup(`Đang phát nhạc: ${musicTitles[rand] || "Bài nhạc chưa có tên"}`);
        popupDisplayed = true; // Đánh dấu rằng popup đã được hiển thị
    }
}

document.addEventListener("click", wussunplay);

audio.addEventListener("ended", function() {
    rand = (rand + 1) % Wussun_Music.length;
    audio.src = Wussun_Music[rand];
    audio.play();
    popupDisplayed = false; // Đặt lại biến trạng thái khi bài nhạc kết thúc
});
